import React, { useState } from 'react'
import { data } from '../../data/data';
import Datashow from '../Datashow';

const Homepage = () => {
    const [links, setLinks] = useState(data);
    const [searchField, setSearchField] = useState('');

    const onSearchChange = (event) => {
        setSearchField(event.target.value)
    }

    const filteredLinks = links.filter((link) => link.title.toLowerCase().includes(searchField.toLowerCase()));

  return (
    <div className='homepage__wrapper'>
        <h1 className='head_text'>
        Streamline access to essential <br className='max-md:hidden'/>
          <span className='orange_gradient'> Resources and Links. </span>
        </h1>


        <div class="search__wrapper">
        <input onChange={onSearchChange} className='homepage__search' type='text' placeholder='Search' />
        </div>

        <Datashow linkList={filteredLinks} />
    </div>
  )
}

export default Homepage
