import React, { useState } from 'react'
import { data } from '../../data/data';
import Datashow from '../Datashow';

const Homepage = () => {
    const [links, setLinks] = useState(data);
    const [searchField, setSearchField] = useState('');
    const [selectField, setSelectField] = useState('All');

    const onSearchChange = (event) => {
        setSearchField(event.target.value)
    }

    const onSelectChange = (event) => {
        setSelectField(event.target.value)
    }

    const selectedLinks = links.filter((link) => link.category && link.category.toLowerCase().includes(selectField.toLowerCase()))

    const filteredLinks = links.filter((link) => link.title.toLowerCase().includes(searchField.toLowerCase()));

    function filterItems(){
        let filtered = links;

        if(selectField !== 'All'){
            filtered = filtered.filter((item) => item.category === selectField)
        }
    }


  return (
    <div className='homepage__wrapper'>
        <h1 className='head_text'>
        Streamline access to essential <br className='max-md:hidden'/>
          <span className='orange_gradient'> Resources and Links. </span>
        </h1>

        <h2 className='desc'>
        Discover a convenient one-stop destination for all your resources and links.
        </h2>

        <div class="search__wrapper">
            <input onChange={onSearchChange} className='homepage__search' type='text' placeholder='Search' />
            <select onChange={onSelectChange}>
                <option value={"All"}>All</option>
                {
                    links.map((link) => link.category && <option key={link.id} value={link.category}>{link.category}</option>)
                }
            </select>
        </div>

        <Datashow linkList={filteredLinks} selectList={selectedLinks} />
    </div>
  )
}

export default Homepage
